mapper.select:
        select
          id, companyname, img, yingyezhizhaohao, faren, phonenumber, jingyingfanwei, address, zhuceshijian, createtime, updatetime, status, createid
        from company


                and id = #{id}


                and companyname like concat('%',#{companyname},'%')


                and img = #{img}


                and yingyezhizhaohao = #{yingyezhizhaohao}


                and faren = #{faren}


                and phonenumber = #{phonenumber}


                and jingyingfanwei = #{jingyingfanwei}


                and address = #{address}


                and zhuceshijian = #{zhuceshijian}


                and createtime = #{createtime}


                and updatetime = #{updatetime}


                and status = #{status}


                and createid = #{createid}


order by createtime desc

select count(1)
from company


        and id = #{id}


        and companyname = #{companyname}


        and img = #{img}


        and yingyezhizhaohao = #{yingyezhizhaohao}


        and faren = #{faren}


        and phonenumber = #{phonenumber}


        and jingyingfanwei = #{jingyingfanwei}


        and address = #{address}


        and zhuceshijian = #{zhuceshijian}


        and createtime = #{createtime}


        and updatetime = #{updatetime}


        and status = #{status}


        and createid = #{createid}
