mapper.select:
        select
        m.id, content, m.createtime, updatetime, senderid, receiveid, status,
               s.username as senderName,r.username as receiverName
        from message m
        left join sysuser s on s.id = m.senderid
        left join sysuser r on r.id = m.receiveid


                and m.id = #{id}


                and content = #{content}


                and m.createtime = #{createtime}


                and updatetime = #{updatetime}


                and senderid = #{senderid}


                and receiveid = #{receiveid}


                and status = #{status}


order by createtime desc

select count(1)
from message


        and id = #{id}


        and content = #{content}


        and createtime = #{createtime}


        and updatetime = #{updatetime}


        and senderid = #{senderid}


        and receiveid = #{receiveid}


        and status = #{status}
