mapper.select:
select
o.id, carid, o.createtime, o.updatetime, o.userid, o.spare1, o.spare2,
       u.username,o.remark,u.phonenumber as account, p.companyid,
       oe.content
from `order` o
left join sysuser u on u.id = o.userid
left join shopcart s on s.id = o.carid
left join product p on p.id = s.productid
left join order_evalute oe on oe.orderid = o.id


        and o.id = #{id}


        and carid = #{carid}


        and p.companyid = #{companyid}



        and o.userid = #{userid}


        and o.spare1 = #{spare1}


        and o.spare2 = #{spare2}


order by createtime desc

select count(1)
from `order`


        and id = #{id}


        and carid = #{carid}


        and createtime = #{createtime}


        and updatetime = #{updatetime}


        and userid = #{userid}


        and spare1 = #{spare1}


        and spare2 = #{spare2}
