mapper.select:
        select
          p.id, productname, productdes, p.img, chengben, kedanjia, kucun, fahuotianshu, chandi, guige,
               companyid, p.createtime, p.status, p.updatetime, producttype, spare1, spare2, detailimg,
               service_start_time, service_end_time,
               t.type_name,
               c.companyname as company_name, c.avg_rating as company_rating,
               c.rating_count as company_rating_count, c.service_area as company_service_area
        from product p
        left join producttype t on t.id = p.producttype
        left join company c on c.id = p.companyid


                and p.id = #{id}


                and productname like concat('%',#{productname},'%')


                and productdes = #{productdes}



                and chengben = #{chengben}


                and kedanjia = #{kedanjia}


                and kucun = #{kucun}


                and fahuotianshu = #{fahuotianshu}


                and chandi = #{chandi}


                and guige = #{guige}


                and companyid = #{companyid}



                and p.status = #{status}



                and producttype = #{producttype}


                and spare1 = #{spare1}


                and spare2 = #{spare2}



                and (

                    (p.service_start_time <= #{serviceEndTime} and p.service_end_time >= #{serviceStartTime})
                    or

                    (p.service_start_time = '08:00' and p.service_end_time = '22:00')
                )



                and c.avg_rating >= #{companyRating}



                and CAST(p.kedanjia AS DECIMAL(10,2)) >= #{minPrice}


                and CAST(p.kedanjia AS DECIMAL(10,2)) <= #{maxPrice}



order by createtime desc

select count(1)
from product p
left join company c on c.id = p.companyid


        and id = #{id}


        and productname = #{productname}


        and productdes = #{productdes}


        and img = #{img}


        and chengben = #{chengben}


        and kedanjia = #{kedanjia}


        and kucun = #{kucun}


        and fahuotianshu = #{fahuotianshu}


        and chandi = #{chandi}


        and guige = #{guige}


        and companyid = #{companyid}


        and createtime = #{createtime}


        and status = #{status}


        and updatetime = #{updatetime}


        and producttype = #{producttype}


        and spare1 = #{spare1}


        and spare2 = #{spare2}



        and (

            (p.service_start_time <= #{serviceEndTime} and p.service_end_time >= #{serviceStartTime})
            or

            (p.service_start_time = '08:00' and p.service_end_time = '22:00')
        )



        and c.avg_rating >= #{companyRating}



        and CAST(p.kedanjia AS DECIMAL(10,2)) >= #{minPrice}


        and CAST(p.kedanjia AS DECIMAL(10,2)) <= #{maxPrice}

select count(1)
from product
where companyid = #{companyId}
