mapper.select:
        select
        u.id, userid, productid, u.createtime, u.spare1,p.productname,p.img
        from userlike u
        left join product p on p.id = u.productid



                and u.id = #{id}


                and userid = #{userid}


                and productid = #{productid}



order by createtime desc

select count(1)
from userlike


        and id = #{id}


        and userid = #{userid}


        and productid = #{productid}


        and createtime = #{createtime}


        and spare1 = #{spare1}

mapper.delete:
delete from userlike


    and id = #{id}


    and userid = #{userid}


    and productid = #{productid}


    and createtime = #{createtime}


    and spare1 = #{spare1}
