mapper.select:
        select
          id, phonenumber, address, zhuceshijian, createtime, updatetime, status, createid,
          service_time, avg_rating, rating_count, service_area
        from company


                and id = #{id}


                and phonenumber = #{phonenumber}


                and address = #{address}


                and zhuceshijian = #{zhuceshijian}


                and createtime = #{createtime}


                and updatetime = #{updatetime}


                and status = #{status}


                and createid = #{createid}


order by createtime desc

select count(1)
from company


        and id = #{id}


        and phonenumber = #{phonenumber}


        and address = #{address}


        and zhuceshijian = #{zhuceshijian}


        and createtime = #{createtime}


        and updatetime = #{updatetime}


        and status = #{status}


        and createid = #{createid}
