mapper.select:
select s.id, s.productid, userid, s.createtime, number, s.spare1, s.spare2, p.id as product_id, p.productname, p.productdes, p.img, p.chengben, p.kedanjia, p.kucun, p.fahuotianshu, p.chandi, p.guige, p.companyid, p.status, p.updatetime, p.producttype,p.spare2 as productspare2 from shopcart s left join product p on p.id = s.productid
and s.productid = #{productid}
and s.userid = #{userid}
and s.spare1 = #{spare1}
order by s.createtime desc

select count(1)
from shopcart


        and id = #{id}


        and productid = #{productid}


        and userid = #{userid}


        and createtime = #{createtime}


        and number = #{number}


        and spare1 = #{spare1}


        and spare2 = #{spare2}
