mapper.select:
select
  id, type_name, create_time, update_time, `status`, `describe`
from t_type


        and id = #{id}


        and type_name like concat('%',#{typeName},'%')


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and status = #{status}


        and `describe` = #{describe}


order by create_time desc

select count(1)
from t_type


        and id = #{id}


        and type_name = #{typeName}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and status = #{status}


        and `describe` = #{describe}
