mapper.select:
        select
        id, title, type, content, create_time, update_time, status, img
        from webnotice


                and id = #{id}


                and title like concat('%',#{title},'%')


                and type = #{type}


                and content = #{content}


                and create_time = #{createTime}


                and update_time = #{updateTime}


                and status = #{status}


                and img = #{img}


order by create_time desc

select count(1)
from webnotice


        and id = #{id}


        and title = #{title}


        and type = #{type}


        and content = #{content}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and status = #{status}


        and img = #{img}
